mapper.insert:

INSERT INTO board(
seq,
title,
content_character,
content_character2,
userID, 
content,
address,
lat,
lng		
)
VALUES(
(SELECT IFNULL(MAX(seq+1), 0) FROM board SUBQUERY)
,#{title}
,#{content_character}
,#{content_character2}		
,#{userID}
,#{content}
,#{address}
,#{lat}
,#{lng}
)

mapper.select:
    SELECT img,img2,img3
    FROM oneteam_img
	WHERE SEQ=#{seq}

mapper.update:
	update oneteam_img set 		
	img = #{img},
	img2 = #{img2},
	img3 = #{img3}
WHERE SEQ=#{seq}
