mapper.select:
SELECT userID, userPW ,userName ,email ,emailAddr , emailOpt, zipNo, roadAddrPart1, addrDetail
, firstNum1, userPhone1, userPhone2
	  FROM oneteam_join
	 WHERE userID = #{userID}
	   AND userPW = #{userPW}

SELECT COUNT(*) FROM oneteam_join
WHERE userID = #{userID}
  AND userPW = #{userPW}

mapper.update:
UPDATE oneteam_join SET zipNo = #{zipNo}, roadAddrPart1 = #{roadAddrPart1}, 
addrDetail = #{addrDetail}
WHERE userID = #{userID}

UPDATE oneteam_join SET firstNum1 = #{firstNum1}, userPhone1 = #{userPhone1}, 
userPhone2 = #{userPhone2}
WHERE userID = #{userID}
